// ================= 数据提取辅助函数 =================
uint16_t get_bitmap_row_data(int digit, int row) {
  if (row >= (int)(sizeof(bitmap_bytes)/40)) return 0;
  int col_idx = (digit == 0) ? 9 : digit - 1;
  long offset = row * 40 + col_idx * 4;
  uint8_t b0 = bitmap_bytes[offset];
  uint8_t b1 = bitmap_bytes[offset + 1];
  return (b0 << 8) | b1;
}

